/* global d3, Jupyter */

export default class Modal extends Renderer {

  constructor({ verbose = false, appendTo, callbackHandler }) {
    super({ verbose: verbose, appendTo: appendTo, callbackHandler: callbackHandler });
  }

  render(json) {
    var self = this;

    var modalId = IDUtils.getWindowId(json.callback.id);
    this.logger.debug(`Creating Callback Modal [${modalId}]...`);

    var overlay = d3.select('body').append('div')
      .attr('class', 'francy overlay');
    var holder = d3.select('body').append('div')
      .attr('class', 'francy');
    var modal = holder.append('div')
      .attr('id', modalId)
      .attr('class', 'francy modal');

    var form = modal.append('form');

    var header = form.append('div').attr('class', 'header');

    header.append('span').html('Required arguments for&nbsp;').append('span').attr('style', 'font-weight: bold;').text(json.title);

    var content = form.append('div').attr('class', 'content');

    for (var arg of Object.values(json.callback.requiredArgs)) {
      content.append('label').attr('for', arg.id).text(arg.title);
      content.append('input').attr('id', arg.id).attr('class', 'arg')
        .attr('required', '')
        .attr('name', arg.id)
        .attr('type', arg.type)
        .attr('value', arg.value)
        .on('change', function() {
          json.callback.requiredArgs[this.id].value = this.value;
        })
        .on('input', this.onchange)
        .on('keyup', this.onchange)
        .on('paste', this.onchange);
      content.append('span').attr('class', 'validity');
      content.append('br');
    }

    var footer = form.append('div').attr('class', 'footer');

    footer.append('button').text('Ok').on('click', function() {
      if (form.node().checkValidity()) {
        self.options.callbackHandler(json.callback);
        overlay.remove();
        modal.remove();
        holder.remove();
        event.preventDefault();
      }
      return false;
    });
    footer.append('button').text('Cancel').on('click', () => {
      event.preventDefault();
      overlay.remove();
      modal.remove();
      holder.remove();
      return false;
    });

    // disable keyboard shortcuts when using this modal in Jupyter
    try {
      Jupyter.keyboard_manager.register_events('.arg');
      Jupyter.keyboard_manager.register_events('.francy .overlay');
      Jupyter.keyboard_manager.register_events('.francy .modal');
    }
    catch (e) {
      if (e.name == 'ReferenceError') {
        self.logger.debug('It seems we\'re not running on Jupyter...', e);
      }
    }

    this.logger.debug(`Callback Modal updated ${modalId}...`);

    return modal;
  }
}

import Base from './base';

export default class Renderer extends Base {

  constructor({ verbose = false, appendTo, callbackHandler }) {
    super({ verbose: verbose, appendTo: appendTo, callbackHandler: callbackHandler });
    if (new.target === Renderer) {
      throw new TypeError('Cannot construct [Renderer] instances directly!');
    }
    if (this.render === undefined || typeof this.render !== 'function') {
      throw new TypeError('Must override [render(json)] method!');
    }
  }

}

import IDUtils from '../util/id-utils';
import Composite from './composite';

/* global d3 */

export default class Window extends Composite {

  constructor({ verbose = false, appendTo, callbackHandler }) {
    super({ verbose: verbose, appendTo: appendTo, callbackHandler: callbackHandler });
  }

  render(json) {
    var windowId = IDUtils.getWindowId(json.canvas.id);
    var window = d3.select(`#${windowId}`);

    // check if the window is already present
    if (!window.node()) {
      // create a div element detached from the DOM!
      this.logger.debug(`Creating Window [${windowId}]...`);
      window = d3.select(this.options.appendTo).append('div').remove()
        .attr('id', windowId)
        .attr('class', 'francy window');
    }

    // cannot continue if window is not present
    if (!window.node()) {
      throw new Error(`Oops, could not create window with id [${windowId}]... Cannot proceed.`);
    }

    this.logger.debug(`Window updated ${windowId}...`);

    this.renderChildren(window, json);

    return window;
  }

}

/**
 * This class holds default methods to handle changes on a model object.
 * It works by the means of a Proxy to track changes and ensures subscribers
 * are notified of these changes on a time basis (1 second default).
 */
export default class Tracker {
  /**
   * Creates a instance of ModelTracker.
   * @param {object} object - the object object to keep track of changes.
   * @param verbose
   */
  constructor(object, { verbose = false, throttle = 1000 } = {}) {
    this.verbose = verbose;
    /**
     * This is property holds a list of change subscribers.
     * @type {function}
     * @private
     */
    this._subscribers = [];
    /**
     * This is property holds a proxy that handles a dirty flag when object changes.
     * @type {Proxy}
     * @private
     */
    this._proxy = new Proxy(object, this);
    /**
     * This is property is used to flag when the object changes.
     * @type {boolean}
     * @private
     */
    this._dirty = false;
    /**
     * Sync listeners every second, if data is dirty
     * @type {setInterval}
     * @private
     */
    setInterval(() => {
      if (this._dirty) {
        this._dirty = false;
        return this.sync();
      }
    }, throttle);
  }

  /**
   * This method is used by the proxy to set a property when a change occurs, plus it sets the current object to dirty.
   * @param {object} receiver - the object being tracked
   * @param {object} property - the property changed
   * @param {object} value - the new value
   */
  set(receiver, property, value) {
    if (!(value[property] instanceof Object) && receiver[property] !== value) {
      if (this.verbose) {
        //console.debug(`Object ID ${this.object.id} property ${property} changed from ${receiver[property]} to ${value}.`);
      }
      receiver[property] = value;
      this._dirty = true;
    }
    return true;
  }

  /**
   * This method is used by the proxy to get the object being tracked
   * @param {object} target - the object being tracked
   * @param {object} key the the object property
   * @returns {object} returns the object being tracked
   */
  get(target, key) {
    if (typeof target[key] === 'object' && target[key] !== null) {
      return new Proxy(target[key], this);
    }
    return key in target ? target[key] : target;
  }

  /**
   * Returns the object being tracked
   * @returns {object} the object properties
   */
  get object() {
    return this._proxy;
  }

  /**
   * This method is used register a function that is invoked to sync the object
   * @param {function} fn - the function to execute - must handle one arg, the object, of type {object}
   */
  subscribe(fn) {
    this._subscribers.push(fn);
  }

  /**
   * This method is used unregister a function registered previously
   * @param {function} fn - the function to execute - must handle one arg, the object, of type {object}
   */
  unsubscribe(fn) {
    this._subscribers = this._subscribers.filter(item => item !== fn ? item : undefined);
  }

  /**
   * This method is used to explicitly sync the module with all the subscribers
   */
  sync() {
    this._subscribers.forEach(item => item.call(this, this.object));
  }
}

